javascript
// jQuery code for simplifying DOM manipulation and AJAX requests

// DOM manipulation
$(document).ready(function() {
  // Code to be executed when the document is ready
});

// AJAX requests
$.ajax({
  url: "https://api.example.com/polls",
  method: "GET",
  success: function(response) {
    // Code to be executed when the request is successful
  },
  error: function(xhr, status, error) {
    // Code to be executed when there is an error
  }
});

// Example of using jQuery to manipulate the DOM
$("#myButton").click(function() {
  // Code to be executed when the button is clicked
});

// Example of making an AJAX request using jQuery
$.ajax({
  url: "https://api.example.com/polls",
  method: "POST",
  data: {
    title: "My Poll",
    options: ["Option 1", "Option 2", "Option 3"]
  },
  success: function(response) {
    // Code to be executed when the request is successful
  },
  error: function(xhr, status, error) {
    // Code to be executed when there is an error
  }
});
