var React = (function() {
  // Private variables and functions
  var polls = [];

  // Public methods
  return {
    createPoll: function(title, options) {
      var poll = {
        title: title,
        options: options,
        responses: []
      };
      polls.push(poll);
      return poll;
    },
    getPolls: function() {
      return polls;
    },
    submitResponse: function(poll, option) {
      var response = {
        poll: poll,
        option: option
      };
      poll.responses.push(response);
    }
  };
})();